package n.edu.iuh.fit.repositories;import n.edu.iuh.fit.entity.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;@Repositorypublic interface RegisterRepository extends JpaRepository<User, Long> {    @Query("SELECT u FROM User u WHERE u.UserName = ?1 AND u.Password = ?2")  User findUserByUserNameAndPassword(String userName, String password);    @Query("SELECT u FROM User u WHERE u.UserName = ?1")    User findByUserName(String userName);}